import retrofit2.http.QueryName;

public interface get_data_from_retrofit {

    String BASE_URL = "https://sandbox-healthservice.priaid.ch/";
    String BODY_LOCATIONS = "body/locations?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..uKvSqmC9YCiYfwZADB0BUD5CreRKZMYNhsm20007ml4&format=json&language=en-gb";
    String BODY_LOCATION = "body/locations/{id}?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..Mkc0CxvjieTZulfsomJIH58Lwze5IASH2qBkApgeqRo&format=json&language=en-gb";
    String BODY_SYMPTOMS = "symptoms/{id}/0?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..p3_QK0_6D8-5fdTZ8zrAQRBx49ZinrgFwUh66-0PmS8&format=json&language=en-gb";
    String BODY_DIAG = "diagnosis";

    Retrofit retrofit = new Retrofit.Builder()
            .baseUrl(BASE_URL)
            .addConverterFactory(GsonConverterFactory.create())
            .build();

    @GET(BODY_LOCATIONS)
    Call<ArrayList<body_locations>> GET_BODY_LOCATIONS();

    @GET(BODY_LOCATION)
    Call<ArrayList<body_locations>> GET_BODY_LOCATION(@Path("id") int body_part_id);

    @GET(BODY_SYMPTOMS)
    Call<ArrayList<body_symptoms>> GET_BODY_SYMPTOMS(@Path("id") int body_part_id);

//    @GET(BODY_DIAG)
//    Call<ArrayList<Item>> GET_BODY_DIAG(@Field("symptoms") int arrayList);

    @GET(BODY_DIAG)
    Call<ArrayList<Item>> GET_BODY_DIAG(@Query("symptoms") String symptoms,
                                        @Query("gender") String gender,
                                        @Query("year_of_birth") Integer year_of_birth,
                                        @Query("token") String token,
                                        @Query("format") String format,
                                        @Query("language") String language);

}
